package com.cth.gestorlotericoweb;

import com.cth.gestorlotericoweb.utils.DadosOcr;
import com.google.api.services.vision.v1.Vision;
import java.io.BufferedReader;
import java.io.DataOutputStream;
import java.io.InputStreamReader;
import java.net.URL;
import javax.net.ssl.HttpsURLConnection;
import javax.servlet.http.HttpServletRequest;

/**
 *
 * @author CarlosEduardo
 */
public class OCR {
    final HttpServletRequest request;
    public String output;
    Vision  vision;

    public OCR(HttpServletRequest request) {
        this.request = request;
        try {
            output = postingtogoogle();
            DadosOcr dadosOcr = new DadosOcr(output, request);
            output = dadosOcr.getPilhaKeyNames().toString();
        } catch (Exception ex) {
            output = ex.getMessage()+output;
        }
    }
    
   
    private String postingtogoogle() throws Exception {
        //    Base64.encode;
        String re;
        String url = "https://vision.googleapis.com/v1/images:annotate?key=AIzaSyBKY3JawFqtxuKI3z-o41xQzxa6_SsGUxc";
        URL obj = new URL(url);
        HttpsURLConnection con = (HttpsURLConnection) obj.openConnection();
        String imgstr =request.getParameter("img_str");//"";;

        //imgstr = encodeToString(img, "png");

        //add reuqest header
        con.setRequestMethod("POST");
        con.setRequestProperty("Content-Type", "application/json; charset=UTF-8");
        String urlParameters = "{\n"
                + "  \"requests\":[\n"
                + "    {\n"
                + "      \"image\":{\n"
                + "        \"content\":\"" + imgstr.split(",")[1] + "\"\n"
                + "      },\n"
                + "      \"features\":[\n"
                + "        {\n"
                + "          \"type\":\"TEXT_DETECTION\",\n"
                + "          \"maxResults\":1\n"
                + "        }\n"
                + "      ]\n"
                + "    }\n"
                + "  ]\n"
                + "}";

        // Send post request
        con.setDoOutput(true);
        try (DataOutputStream wr = new DataOutputStream(con.getOutputStream())) {
            wr.writeBytes(urlParameters);
            wr.flush();
        }

        StringBuilder response;
        try (BufferedReader in = new BufferedReader(
                new InputStreamReader(con.getInputStream()))) {
            String inputLine;
            response = new StringBuilder();
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }   
        }

        return response.toString();

    }
}
